            ),
        ));
        $this->html($args);
    }
    public function html($args){ ?>
        <aside class="cs_app_sidepanel ">
                <div class="sidepanel_inner">
                    <ul>
                        <?php foreach($this->menu_items as $menu_item_slug => $menu_item): ?>
                        <li>
                            <a href="?page=<?php echo $menu_item['href'] ?>" class="<?php echo $args['page']==$menu_item_slug?'active':''; ?>">
                                <i class="icon">
                                    <?php _e($menu_item['icon']) ?>
                                </i>
                                <p><?php _e($menu_item['label']) ?></p>
                            </a>
                        </li>
                        <?php endforeach; ?>
                    </ul>
                </div>
            </aside>
    <?php }
}